mapper.select:
select * from dish

    name like concat('%', name, ',')
    and category_id = #{categoryId}
    and price = #{price}
    and image = #{image}
    and description = #{description}
    and status = #{status}
    and create_time = #{createTime}
    and update_time = #{updateTime}
    and create_user = #{createUser}
    and update_user = #{updateUser}
